import { reduxForm } from "redux-form";
import React from 'react';
import classes from "./Form.module.css";

const HeroForm = (props) => {
    return(
        <form action="http://localhost:80/superheroes/back/server.php">
            <div className={classes.formBlock}>
                <img src={props.baseUrl + props.heroProfile.image}></img>
                <div className={classes.field}>
                    <label>Name: </label>
                    <input placeholder="Name" type="text" value={props.heroProfile.nickname}></input>
                </div>
                <div className={classes.field}>
                    <label>Real name: </label>
                    <input placeholder="Real name" value={props.heroProfile.realname}></input>
                </div>
                <div className={classes.field}>
                    <label>Description: </label>
                    <textarea placeholder="Description" value={props.heroProfile.description}></textarea>
                </div>
                <div className={classes.field}>
                    <label>Superpower: </label>
                    <textarea placeholder="Superpower" value={props.heroProfile.superpower}></textarea>
                </div>
                <div className={classes.field}>
                    <label>Catch phrase: </label>
                    <textarea placeholder="Catch phrase" value={props.heroProfile.catch_phase}></textarea>
                </div>
                <input type="submit" value="Save"></input>
            </div>
        </form>
    )

}

const HeroReduxForm = reduxForm({
    form: 'heroForm'
})(HeroForm);
const Form = (props) => {
    return (
        <div>
            <HeroReduxForm heroProfile={props.heroProfile} baseUrl={props.baseUrl} />
        </div>
    );
}

export default Form;
