import React from 'react';
import classes from './Heroes.module.css';
import { NavLink, Link } from 'react-router-dom';

import Hero from './Hero/Hero';


let newInfoElement = React.createRef();

let onInfoChange = () =>{
    let text = newInfoElement.current.value;
    props.updateHeroInfo(text);
}


const Heroes = (props) => {

    let pagesCount = Math.ceil(props.totalHeroesCount / props.pageSize);

    let baseUrl = "http://localhost:80/superheroes/";

    let pages = [];

    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }

    let heroProfile = {}

    let heroes = props.heroes.map((hero, index) => {
        return <Hero id={hero.id}
            photo={hero.images}
            nickname={hero.nickname}
            realname={hero.real_name}
            description={hero.origin_description}
            superpower={hero.superpowers}
            catch_phase={hero.catch_phase}
            showHero={props.showHero} 
            baseUrl={baseUrl}/>
    });

    heroes.map(hero => {
        if (hero.props.id == props.currentHero){
            console.log(hero);
            heroProfile = {
                id: hero.props.id,
                nickname: hero.props.nickname,
                realname: hero.props.realname,
                description: hero.props.description,
                superpower: hero.props.superpower,
                catch_phase: hero.props.catch_phase,
                image: hero.props.photo
            }
            return heroProfile;
        }
    });
    console.log(heroProfile);
    
    
    return (
        <div className={classes.UI}>
            <div className={classes.heroes}>
                <div className={classes.hero}>
                    <form action="http://localhost:80/superheroes/back/server.php">
                        <div className={classes.formBlock}>
                            <img src={baseUrl + heroProfile.image}></img>
                            <div className={classes.field}>
                                <label>Name: </label>
                                <input placeholder="Name" value={heroProfile.nickname} ref={newInfoElement} onInfoChange={onInfoChange}></input>
                            </div>
                            <div className={classes.field}>
                                <label>Real name: </label>
                                <input placeholder="Real name" value={heroProfile.realname}></input>
                            </div>
                            <div className={classes.field}>
                                <label>Description: </label>
                                <textarea placeholder="Description" value={heroProfile.description}></textarea>
                            </div>
                            <div className={classes.field}>
                                <label>Superpower: </label>
                                <textarea placeholder="Superpower" value={heroProfile.superpower}></textarea>
                            </div>
                            <div className={classes.field}>
                                <label>Catch phrase: </label>
                                <textarea placeholder="Catch phrase" value={heroProfile.catch_phase}></textarea>
                            </div>
                            <input type="submit" value="Save"></input>
                        </div>
                        
                    </form>
                </div>
            </div>
            <div className={classes.heroes}>
                {heroes}
                <div className={classes.pagination}>
                    <span onClick={props.prevPage}>←</span>
                    {
                        pages.map(p => {
                            if (p > props.min && props.max) {
                                return <span className={props.currentPage === p & classes.selectedPage}
                                    onClick={() => { props.onPageChanged(p) }}>{p}</span>
                            }
                        })
                    }
                    <span onClick={props.nextPage}>→</span>
                </div>
            </div>
        </div>

    );
}

export default Heroes;
