import React from 'react';
import classes from './Hero.module.css';
import userPhotoDefault from '../../../assets/user.png';

const Hero = (props) => {
    return (
        <div key={props.id} className={classes.hero} onClick={() => props.showHero(props.id)}>
            <div className={classes.heroUI}>
                <img src={props.photo != null ? props.baseUrl + props.photo : userPhotoDefault}></img>
                <p>{props.nickname}</p>
            </div>
        </div>
    );
}

export default Hero;
