import React from 'react';
import classes from './Heroes.module.css';
import { NavLink, Link } from 'react-router-dom';
import userPhotoDefault from '../../assets/user.png';
import Hero from './Hero/Hero';





const Heroes = (props) =>{

    let pagesCount = Math.ceil(props.totalHeroesCount / props.pageSize);

    let pages = [];

    for(let i = 1; i <= pagesCount; i++){
        pages.push(i);
    }

    let heroProfile = {
        id: null,
        nickname: null,
        realname: null,
        description: null,
        catch_phase: null,
        photo: null
    }

    let heroes = props.heroes.map((hero, index) => {
        return <Hero id={hero.id}
                    photo={hero.images}
                    nickname={hero.nickname}
                    realname={hero.real_name}
                    description={hero.origin_description}
                    superpower={hero.superpowers}
                    catch_phase={hero.catch_phase}
                    showHero={props.showHero}/>
    });
    
    heroes.map(hero => {
        if(hero.id == props.currentPhoto){
            heroProfile = {
                id: hero.id,
                nickname: hero.nickname,
                realname: hero.real_name,
                description: hero.origin_description,
                superpower: hero.superpowers,
                catch_phase: hero.catch_phase,

            }
            return heroProfile;
        }
    });

    return(
        
        <div className={classes.heroes}>
            {
                props.heroes.map(h => 
                    <div key = {h.id} className={classes.hero}>
                        <div className={classes.heroUI}>
                            <NavLink to={'/hero/' + h.id} className={classes.link}>
                                <img src={h.images != null ? h.images : userPhotoDefault}></img>
                                <p>{h.nickname}</p>
                            </NavLink>
                        </div>
                    </div>  
                )
            }
            <div className={classes.pagination}>
                <span onClick={props.prevPage}>←</span>
                {
                    pages.map(p => {
                        if(p > props.min && props.max){
                            return <span className={props.currentPage === p & classes.selectedPage}
                            onClick={()=>{props.onPageChanged(p)}}>{p}</span>
                        }
                    })
                }
                <span onClick={props.nextPage}>→</span>
            </div>
        </div>
    );
}

export default Heroes;
