import { Field, reduxForm } from "redux-form";
import React from 'react';
import classes from "./Form.module.css";
import { connect } from "react-redux";


class AddForm extends React.Component {
    
    constructor(props){
        super(props);
        this.state={
            nickname: '',
            realname: '',
            description: '',
            superpower: '',
            catch_phase: ''
        }
    }

    render(){
        const {nickname, realname, description, superpower, catch_phase} = this.state;
        return (
            <form className={classes.form}>
                <h2>Add new Hero</h2>
                <div className={classes.formBlock}>
                    <div className={classes.field}>
                        <label>Name: </label>
                        <input placeholder="Name" name="nickname" type="text" value={nickname} onChange={this.changeHandler}/>
                    </div>
                    <div className={classes.field}>
                        <label>Real name: </label>
                        <input placeholder="Real name" name="realname" value={realname}/>
                    </div>
                    <div className={classes.field}>
                        <label>Description: </label>
                        <textarea placeholder="Description" name="description" value={description} />
                    </div>
                    <div className={classes.field}>
                        <label>Superpower: </label>
                        <textarea placeholder="Superpower" name="superpower" value={superpower} />
                    </div>
                    <div className={classes.field}>
                        <label>Catch phrase: </label>
                        <textarea placeholder="Catch phrase" name="catch_phase" value={catch_phase}/>
                    </div>
                    <input type="file"></input>
                    <input type="submit" value="Save"></input>
                </div>
            </form>
        )
    }
}
const EditForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit} className={classes.form}>
            <h2>Edit Hero</h2>
            <div className={classes.formBlock}>
                <img src={props.baseUrl + props.heroProfile.image}></img>
                <div className={classes.field}>
                    <label>Name: </label>
                    <Field placeholder="Name" name={"nickname"} type="text" value={props.heroProfile.nickname} component={"input"} />
                </div>
                <div className={classes.field}>
                    <label>Real name: </label>
                    <Field placeholder="Real name" name={"realname"} value={props.heroProfile.realname} component={"input"} />
                </div>
                <div className={classes.field}>
                    <label>Description: </label>
                    <Field placeholder="Description" name={"description"} value={props.heroProfile.description} component={"textarea"} />
                </div>
                <div className={classes.field}>
                    <label>Superpower: </label>
                    <Field placeholder="Superpower" name={"superpower"} value={props.heroProfile.superpower} component={"textarea"} />
                </div>
                <div className={classes.field}>
                    <label>Catch phrase: </label>
                    <Field placeholder="Catch phrase" name={"catch_phase"} value={props.heroProfile.catch_phase} component={"textarea"} />
                </div>
                <input type="submit" value="Save"></input>
            </div>
        </form>
    )

}





const HeroForm = (props) => {
    // const AddReduxForm = reduxForm({form: 'addForm'})(AddForm);
    const EditReduxForm = reduxForm({form: 'editForm'})(EditForm);
    return (
        <>
            {props.heroProfile.image ?
                <div>
                    {
                        props.heroProfile.currentDoing === "Show" ?
                            <div className={classes.showHero}>
                                <img src={props.baseUrl + props.heroProfile.image}></img>
                                <p>{props.heroProfile.nickname}</p>
                                <p>{props.heroProfile.realname}</p>
                                <p>{props.heroProfile.description}</p>
                                <p>{props.heroProfile.superpower}</p>
                                <p>{props.heroProfile.catch_phase}</p>
                            </div>
                            : props.heroProfile.currentDoing === "Add" ?
                                <AddForm heroProfile={props.heroProfile} savePhoto={props.savePhoto} onSubmit={props.onSubmit} addHero={props.addHero}/>
                                :
                                <EditReduxForm heroProfile={props.heroProfile} baseUrl={props.baseUrl} onSubmit={props.onSubmit}/>
                    }
                </div>
                : <AddForm heroProfile={props.heroProfile} savePhoto={props.savePhoto}onSubmit={props.onSubmit} addHero={props.addHero} />
            }
        </>
    )

}

// const HeroReduxForm = reduxForm({ form: 'heroForm' })(HeroForm);
class FormContainer extends React.Component {

    constructor(props){
        super(props);
        this.state={
            nickname: '',
            realname: '',
            description: '',
            superpower: '',
            catch_phase: ''
        }
    }
    // onSubmit = (formData) => {
    //     addHero(formData.nickname, formData.realname, formData.description, formData.superpower, formData.catch_phase);
    // }
    render(){
        return (
            <HeroForm heroProfile={this.props.heroProfile} baseUrl={this.props.baseUrl} savePhoto={this.savePhoto} addHero={this.addHero}/>
        );
    }
    
}

export default connect(null, null)(FormContainer);
