import React from 'react';
import classes from './Form.module.css';
import axios from 'axios';


class AddForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nickname: '',
            realname: '',
            description: '',
            superpower: '',
            catch_phase: '',
            photo: null
        }
    }
    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value });      
    }
    fileSelectedHandler = e => {
        this.setState({
            photo: e.target.files[0]
        })
    }

    submitHandler = (e) => {
        e.preventDefault();
        console.log(this.state);
        axios.post(`http://localhost:80/superheroes/back/server.php`, this.state)
        .then(resposne=>{
            console.log(resposne)
        })
        .catch(error => {
            console.log(error);
        })
    }
    render() {
        const {nickname,realname,description,superpower,catch_phase} = this.state;
        return (
            <form className={classes.form} onSubmit={this.submitHandler}>
                <h2>Add new Hero</h2>
                <div className={classes.formBlock}>
                    <div className={classes.field}>
                        <label>Name: </label>
                        <input placeholder="Name" value={nickname} name="nickname" type="text" onChange={this.changeHandler}/>
                    </div>
                    <div className={classes.field}>
                        <label>Real name: </label>
                        <input placeholder="Real name" value={realname} name="realname"  onChange={this.changeHandler}/>
                    </div>
                    <div className={classes.field}>
                        <label>Description: </label>
                        <textarea placeholder="Description" value={description} name="description"  onChange={this.changeHandler}/>
                    </div>
                    <div className={classes.field}>
                        <label>Superpower: </label>
                        <textarea placeholder="Superpower" value={superpower} name="superpower" onChange={this.changeHandler}/>
                    </div>
                    <div className={classes.field}>
                        <label>Catch phrase: </label>
                        <textarea placeholder="Catch phrase" value={catch_phase} name="catch_phase" onChange={this.changeHandler}/>
                    </div>
                    <input type="file" onChange={this.fileSelectedHandler}></input>
                    <input type="submit" value="Add" name="addHero"></input>
                </div>
            </form>
        )
    }
}

export default AddForm;
